function initAlert() {
  function displayAlert() {
    var bodyRef = document.body || document.querySelector('body');
    var alertContainer = document.createElement('div');
    alertContainer.id = 'pro-portal-alert';
    bodyRef.appendChild(alertContainer);

    var alertCss = ".alert-Container{display:flex;position:absolute;top:0;left:0;z-index:9999;background-color:rgba(0,0,0,.5);width:100vw;height:100vh}.alert-Container *{color:#222;font-family:canada-type-gibson,Helvetica,Arial,sans-serif!important}.alert-HeaderTitle{display:inline-block;margin:16px;background-image:url(https://cdn.ramseysolutions.net/media/b2b/pro-portal/images/brush.svg);background-position:center bottom;background-repeat:no-repeat;background-size:90% 24px;padding:0;line-height:1.5;color:#003561;font-size:40px;font-weight:800}.alert-Header{display:flex;justify-content:center;width:100%;max-width:400px}.portal-LogoContainer{display:flex;justify-content:center;margin-bottom:30px;background-image:url(https://cdn.ramseysolutions.net/media/b2b/pro-portal/images/brush.svg);background-position:center;background-repeat:no-repeat;background-size:100%;width:75%;max-width:400px}.portal-LogoContainer>img{width:75%;max-width:300px}.alert-Card{display:flex;align-items:center;flex-direction:column;justify-content:center;margin:auto;border-radius:8px;box-shadow:rgba(31,36,38,.15) 0 6px 16px 0;background:#fff;padding:24px;padding-bottom:0;max-width:550px;width:100%;height:75%;min-height:200px;color:#222}@supports (display:grid){.alert-Card{height:auto}}.alert-Body{display:flex;align-items:center;flex-direction:column;justify-content:center;margin-top:0;margin-bottom:0;width:100%;padding:16px;padding-bottom:0;text-align:center}p{width:100%;text-align:center;line-height:20px;font-size:20px}.alert-Card h1{width:auto}.alert-Card h2{color:#485257;font-size:24px;font-weight:600}.alert-Button{background:0}.alert-Button{display:-webkit-inline-box;display:inline-flex;-webkit-box-align:center;align-items:center;flex-shrink:0;-webkit-box-pack:center;justify-content:center;-webkit-transition:background-color .3s cubic-bezier(.19,1,.22,1),border-color .3s cubic-bezier(.19,1,.22,1),color .3s cubic-bezier(.19,1,.22,1);transition:background-color .3s cubic-bezier(.19,1,.22,1),border-color .3s cubic-bezier(.19,1,.22,1),color .3s cubic-bezier(.19,1,.22,1);margin:6px;border:2px solid transparent;border-radius:4px;box-shadow:none;background:0;background-color:#0073b9;cursor:pointer;padding:6px 16px;width:auto;text-align:center;text-decoration:none;line-height:24px;color:#fff;font-size:16px;font-weight:600;word-break:break-word}a:focus,a:hover{text-decoration:none;color:#fff}.alert-CTA{margin:16px;padding:12px 20px;line-height:32px;font-size:20px;box-sizing:content-box}.alert-CTA *{text-decoration:none;color:#fff}.alert-Button:focus,.alert-Button:hover{background-color:#0091d9;color:#fff}.alert-Button--borderless{-webkit-transition:color .3s ease,box-shadow .3s ease;transition:color .3s ease,box-shadow .3s ease;margin:0;border:0;border-radius:0;box-shadow:0 2px 0 transparent;background-color:transparent;padding:0;height:auto;color:#0073b9;font-size:20px;font-weight:400}.alert-Button--borderless:focus,.alert-Button--borderless:hover{box-shadow:0 2px 0 currentColor;background-color:transparent;text-decoration:none;color:#0073b9;color:#047}.alert-Button--ghost{border-color:currentColor;background-color:transparent;color:#0073b9}.alert-Button--ghost:focus,.alert-Button--ghost:hover{background-color:rgba(0,115,185,.1);color:#0091d9}body.alert-open{overflow:hidden;height:100vh;}.alert-Logo{display:inline-block;line-height:1.5;color:#003561;font-size:30px}.alert-Note{display:block;margin-top:30px;margin-bottom:16px;font-size:16px}.alert-NoteTitle{font-weight:600}";

    var alertContainerHTML = "<link rel=\"stylesheet\" href=\"https://use.typekit.net/fdp3qaw.css\" /> <style>" + alertCss + "</style> <div class=\"alert-Container\"> <div class=\"alert-Card\"> <div class=\"alert-Header\"> <img class=\"alert-HeaderImage\" src=\"https://cdn.ramseysolutions.net/education/ramseyclassroom/user_facing/We%27ve_upgraded.png\" alt=\"We've Upgraded!\" > </div> <div class=\"alert-Body\"> <div class=\"portal-LogoContainer\"> <img src=\"https://cdn.ramseysolutions.net/media/b2b/pro-portal/logos/portal-logo.svg\" alt=\"Pro Portal\"> </div> <h2>It's time to switch over to the Pro Portal!</h2> <p> <span id=\"easterEgg\">Don't worry.</span> The platform will look different, but all of your information will be the same. So, what are you waiting for? </p> <a href=\"https://pro.daveramsey.com/\"> <button class=\"alert-Button alert-CTA\" type=\"button\"> Continue to Pro Portal! </button> </a> <button class=\"alert-Button alert-Button--ghost\" type=\"button\" id=\"close-modal\"> Remind Me Tomorrow </button> <p> <span class=\"alert-Note\"> <svg class=\"gzl-Icon\" id=\"icon-t-warning\" xmlns=\"http://www.w3.org/2000/svg\" width=\"16\" height=\"16\" viewBox=\"0 0 22 21\"><g fill=\"#fcd214\" fill-rule=\"evenodd\" transform=\"translate(1 1)\"><polygon stroke=\"#495257\" stroke-linecap=\"round\" stroke-linejoin=\"round\" stroke-width=\"2\" points=\"0 19 10 0 20 19\"/><path stroke=\"#495257\" stroke-linecap=\"round\" stroke-linejoin=\"round\" stroke-width=\"2\" d=\"M10,8 L10,12\"/><circle cx=\"10\" cy=\"15\" r=\"1\" fill=\"#495257\" fill-rule=\"nonzero\"/></g></svg> <span class=\"alert-NoteTitle\">NOTE: </span>This site will soon be shut down. </span> </p> </div> </div> </div>";

    alertContainer.innerHTML = alertContainerHTML;

    var close = document.getElementById('close-modal');
    var closeForever = document.getElementById('easterEgg');

    window.scrollTo(0,0);

    bodyRef.className += ' alert-open';

    function closeAlert() {
      bodyRef.className = bodyRef.className.replace(' alert-open', '');
      alertContainer.parentNode.removeChild(alertContainer);
    }

    close.addEventListener('click', function () {
      closeAlert();
    });

    if (closeForever) {
      closeForever.addEventListener('dblclick', function () {
        alert(
          'Hey Ramsey team member - This alert will not be displayed any longer!'
        );
        closeAlert();
        setNeverDisplay();
      });
    }
  }

  function setAlertTime() {
    var now = new Date().getTime();
    localStorage.setItem('proAlert', now);
  }

  function setNeverDisplay() {
    // to be ran if easter egg clicked, by team members
    localStorage.setItem('proAlert', 0);
  }

  function shouldDisplayModal() {
    // returns true if the last time the alert was displayed was a date other than today
    var alertTime = parseInt(localStorage.getItem('proAlert'));
    var then = new Date(alertTime);
    var now = new Date();

    if (alertTime === 0) {
      return false;
      // never display modal
    }

    return now.getDate() !== then.getDate();
  }

  if (!localStorage.getItem('proAlert') || shouldDisplayModal()) {
    // alert has not been opened previously
    // or, alert has been opened and should be opened again
    displayAlert();
    setAlertTime();
  }
}

initAlert();
